using System;\nusing System.IO;\nusing System.Net;\nusing System.Net.Security;\nusing System.Security.Cryptography.X509Certificates;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string url = "https://example.com";\n        Uri uri = new Uri(url);\n\n        // Create the WebRequest\n        WebRequest webRequest = WebRequest.Create(uri);\n\n        // Optional: validate SSL certificate\n        ServicePointManager.ServerCertificateValidationCallback = ValidateServerCertificate;\n\n        // Get the response\n        using (WebResponse webResponse = webRequest.GetResponse())\n        {\n            using (Stream responseStream = webResponse.GetResponseStream())\n            {\n                ReadFrom(responseStream);\n            }\n        }\n    }\n\n    private static void ReadFrom(Stream responseStream)\n    {\n        using (StreamReader reader = new StreamReader(responseStream))\n        {\n            string responseText = reader.ReadToEnd();\n            Console.WriteLine(responseText);\n        }\n    }\n\n    // Custom validation callback for SSL certificates\n    public static bool ValidateServerCertificate(object sender, X509Certificate certificate, X509Chain chain, SslPolicyErrors sslPolicyErrors)\n    {\n        // In a real-world scenario, you should validate the certificate properly\n        // For this example, we'll just accept any certificate\n        return true;\n    }\n}