using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        string inputFolder = @"C:\path\to\your\images\";\n        string outputFile = @"C:\path\to\output\video.mp4";\n        int frameRate = 30; // Set your desired frame rate\n\n        CreateVideoFromImages(inputFolder, outputFile, frameRate);\n    }\n\n    static void CreateVideoFromImages(string inputFolder, string outputFile, int frameRate)\n    {\n        string ffmpegPath = @"C:\path\to\ffmpeg.exe"; // Path to ffmpeg.exe\n\n        ProcessStartInfo startInfo = new ProcessStartInfo\n        {\n            FileName = ffmpegPath,\n            Arguments = $"-framerate {frameRate} -i \"{inputFolder}%04d.jpg\" -c:v libx264 -r {frameRate} -pix_fmt yuv420p \"{outputFile}\"",\n            UseShellExecute = false,\n            RedirectStandardOutput = true,\n            RedirectStandardError = true,\n            CreateNoWindow = true\n        };\n\n        using (Process process = new Process { StartInfo = startInfo })\n        {\n            process.Start();\n            process.WaitForExit();\n            \n            string output = process.StandardOutput.ReadToEnd();\n            string error = process.StandardError.ReadToEnd();\n            \n            Console.WriteLine(output);\n            Console.WriteLine(error);\n        }\n    }\n}