using System;\nusing System.Threading;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a semaphore that can be used across processes\n        bool createdNew;\n        Semaphore semaphore = new Semaphore(1, 1, "Global\\MySemaphore", out createdNew);\n\n        try\n        {\n            Console.WriteLine("Waiting to enter the semaphore...");\n            // Wait to enter the semaphore\n            semaphore.WaitOne();\n            Console.WriteLine("Entered the semaphore.");\n\n            // Perform your work here\n            Thread.Sleep(10000); // Simulate work\n\n            Console.WriteLine("Work done, leaving the semaphore.");\n        }\n        finally\n        {\n            // Release the semaphore\n            semaphore.Release();\n        }\n\n        Console.WriteLine("Press any key to exit.");\n        Console.ReadKey();\n    }\n}