<%@ Control Language="C#" AutoEventWireup="true" CodeBehind="YourControl.ascx.cs" Inherits="YourNamespace.YourControl" %>\n\n<script runat="server">\n    private static bool scriptIncluded = false;\n\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!scriptIncluded)\n        {\n            ClientScript.RegisterStartupScript(this.GetType(), "scriptCheck", @"\n                <script type='text/javascript'>\n                    (function() {\n                        var scriptUrl = 'file.js';\n                        var scripts = document.getElementsByTagName('script');\n                        var scriptFound = false;\n\n                        for (var i = 0; i < scripts.length; i++) {\n                            if (scripts[i].src.indexOf(scriptUrl) > -1) {\n                                scriptFound = true;\n                                break;\n                            }\n                        }\n\n                        if (!scriptFound) {\n                            var script = document.createElement('script');\n                            script.src = scriptUrl;\n                            script.type = 'text/javascript';\n                            document.getElementsByTagName('head')[0].appendChild(script);\n                        }\n                    })();\n                </script>", false);\n            scriptIncluded = true;\n        }\n    }\n</script>