using System.Web;\nusing System.Web.Mvc;\n\npublic class AdminAuthorizeAttribute : ActionFilterAttribute\n{\n    public override void OnActionExecuting(ActionExecutingContext filterContext)\n    {\n        // Check if the user is logged in by checking the session\n        var userId = filterContext.HttpContext.Session["UserId"];\n        var isAdmin = filterContext.HttpContext.Session["IsAdmin"];\n\n        if (userId == null || isAdmin == null || !(bool)isAdmin)\n        {\n            // If the user is not logged in or not an admin, redirect to login page\n            filterContext.Result = new RedirectToRouteResult(new System.Web.Routing.RouteValueDictionary\n            {\n                { "controller", "Account" },\n                { "action", "Login" }\n            });\n        }\n\n        base.OnActionExecuting(filterContext);\n    }\n}