using System;\nusing System.Runtime.InteropServices;\n\n[ComImport]\n[Guid("Your-COM-Object-Guid")]\n[InterfaceType(ComInterfaceType.InterfaceIsIUnknown)]\ninterface IMyObject\n{\n    void MemAlloc(ref IntPtr test, int membercount);\n    void MemFree(IntPtr test);\n}\n\nclass Program\n{\n    static void Main()\n    {\n        IMyObject myObject = (IMyObject)new MyObject(); // Assuming MyObject is the COM class\n\n        IntPtr testPtr = IntPtr.Zero;\n        int memberCount = 100;\n\n        // Allocate memory\n        myObject.MemAlloc(ref testPtr, memberCount);\n\n        // Use the allocated memory (example)\n        double[] managedArray = new double[memberCount];\n        Marshal.Copy(testPtr, managedArray, 0, memberCount);\n\n        // Free the allocated memory\n        myObject.MemFree(testPtr);\n    }\n}