public class Global : System.Web.HttpApplication\n{\n    protected void Application_Start(object sender, EventArgs e)\n    {\n        // Register the session extension route\n        RouteTable.Routes.MapPageRoute("ExtendSessionRoute", "ExtendSession", "~/ExtendSessionHandler.ashx");\n    }\n}\n\npublic class ExtendSessionHandler : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        context.Session.Timeout = 30; // Extend session timeout to 30 minutes\n        context.Response.StatusCode = 200;\n        context.Response.End();\n    }\n\n    public bool IsReusable\n    {\n        get { return false; }\n    }\n}