using (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n    using (SqlCommand cmd = new SqlCommand("SELECT YourXmlColumn FROM YourTable WHERE YourCondition", connection))\n    {\n        using (SqlDataReader reader = cmd.ExecuteReader())\n        {\n            if (reader.Read())\n            {\n                using (XmlReader xmlReader = XmlReader.Create(reader.GetXmlReader(0)))\n                {\n                    DataContractSerializer deserializer = new DataContractSerializer(typeof(YourObjectType));\n                    return deserializer.ReadObject(xmlReader);\n                }\n            }\n        }\n    }\n}