using System;\nusing System.Security.Cryptography.X509Certificates;\n\npublic class SmartCardCertificates\n{\n    public static void Main()\n    {\n        // Specify the SmartCard reader\n        X509Store store = new X509Store("My", StoreLocation.CurrentUser);\n\n        try\n        {\n            store.Open(OpenFlags.ReadOnly);\n\n            foreach (X509Certificate2 cert in store.Certificates)\n            {\n                // Check if the certificate is from the SmartCard\n                if (cert.HasPrivateKey && cert.PrivateKey is RSACryptoServiceProvider)\n                {\n                    RSACryptoServiceProvider rsa = (RSACryptoServiceProvider)cert.PrivateKey;\n                    if (rsa.CspKeyContainerInfo.HardwareDevice)\n                    {\n                        Console.WriteLine("Found SmartCard certificate: " + cert.Subject);\n                        // Use this certificate for your operations\n                        MyRSACryptoProvider(rsa);\n                    }\n                }\n            }\n        }\n        finally\n        {\n            store.Close();\n        }\n    }\n\n    private static void MyRSACryptoProvider(RSACryptoServiceProvider rsa)\n    {\n        // Your logic to use the RSACryptoServiceProvider\n        Console.WriteLine("Using RSACryptoServiceProvider from SmartCard.");\n    }\n}