using System;\nusing System.DirectoryServices;\n\nclass Program\n{\n    static void Main()\n    {\n        string websiteName = "YourWebsiteName"; // Replace with your website name\n        string appPoolName = GetAppPoolName(websiteName);\n\n        if (!string.IsNullOrEmpty(appPoolName))\n        {\n            Console.WriteLine("Application Pool Name: " + appPoolName);\n        }\n        else\n        {\n            Console.WriteLine("Application Pool Name not found.");\n        }\n    }\n\n    static string GetAppPoolName(string websiteName)\n    {\n        string appPoolName = null;\n        try\n        {\n            DirectoryEntry w3svc = new DirectoryEntry("IIS://localhost/W3SVC");\n            foreach (DirectoryEntry site in w3svc.Children)\n            {\n                if (site.SchemaClassName == "IIsWebServer")\n                {\n                    DirectoryEntry siteRoot = new DirectoryEntry(site.Path + "/Root");\n                    if (site.Properties["ServerComment"].Value.ToString().Equals(websiteName, StringComparison.OrdinalIgnoreCase))\n                    {\n                        appPoolName = siteRoot.Properties["AppPoolId"].Value.ToString();\n                        break;\n                    }\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n        return appPoolName;\n    }\n}