// Example of adding a navigation pane\nprivate void InitializeNavigationPane()\n{\n    TreeView navigationTreeView = new TreeView();\n    navigationTreeView.Nodes.Add("Client Information");\n    navigationTreeView.Nodes.Add("Medical Records");\n    navigationTreeView.Nodes.Add("Social History");\n    // Add more nodes as needed\n    navigationTreeView.AfterSelect += NavigationTreeView_AfterSelect;\n    this.Controls.Add(navigationTreeView);\n}\n\nprivate void NavigationTreeView_AfterSelect(object sender, TreeViewEventArgs e)\n{\n    // Load the corresponding form or panel\n    LoadForm(e.Node.Text);\n}