using System;\nusing System.Diagnostics;\nusing System.Windows.Forms;\n\npublic class MainForm : Form\n{\n    private TabControl tabControl;\n    private Button newTabButton;\n\n    public MainForm()\n    {\n        tabControl = new TabControl { Dock = DockStyle.Fill };\n        newTabButton = new Button { Text = "New Tab", Dock = DockStyle.Top };\n        newTabButton.Click += NewTabButton_Click;\n\n        this.Controls.Add(tabControl);\n        this.Controls.Add(newTabButton);\n    }\n\n    private void NewTabButton_Click(object sender, EventArgs e)\n    {\n        // Start a new process for each tab\n        ProcessStartInfo startInfo = new ProcessStartInfo\n        {\n            FileName = Application.ExecutablePath,\n            Arguments = "child"\n        };\n        Process process = Process.Start(startInfo);\n\n        // Optionally, you can add a placeholder tab\n        TabPage tabPage = new TabPage($"Tab {tabControl.TabCount + 1}");\n        tabControl.TabPages.Add(tabPage);\n    }\n\n    [STAThread]\n    public static void Main(string[] args)\n    {\n        if (args.Length > 0 && args[0] == "child")\n        {\n            // Child process logic\n            Application.Run(new ChildForm());\n        }\n        else\n        {\n            // Main process logic\n            Application.EnableVisualStyles();\n            Application.SetCompatibleTextRenderingDefault(false);\n            Application.Run(new MainForm());\n        }\n    }\n}\n\npublic class ChildForm : Form\n{\n    public ChildForm()\n    {\n        this.Text = "Child Process";\n    }\n}