using (var context = new YourDbContext())\n{\n    var data = context.YourEntities.ToList();\n    var bindingList = new BindingList<YourEntity>(data);\n    var source = new BindingSource(bindingList, null);\n\n    yourDataGridView.DataSource = source;\n\n    // Handle saving changes\n    bindingList.ListChanged += (sender, e) =>\n    {\n        if (e.ListChangedType == ListChangedType.ItemChanged)\n        {\n            context.SaveChanges();\n        }\n    };\n}\n\n// Handling row deletion\nyourDataGridView.UserDeletingRow += (sender, e) =>\n{\n    var entity = (YourEntity)e.Row.DataBoundItem;\n    using (var context = new YourDbContext())\n    {\n        context.YourEntities.Attach(entity);\n        context.YourEntities.Remove(entity);\n        context.SaveChanges();\n    }\n};