using System.ServiceModel;\n\nclass ServiceRegistration\n{\n    static void Main(string[] args)\n    {\n        ChannelFactory<IServiceRegistry> factory = new ChannelFactory<IServiceRegistry>(new BasicHttpBinding(), new EndpointAddress("http://localhost:8080/ServiceRegistry"));\n        IServiceRegistry registry = factory.CreateChannel();\n\n        registry.RegisterService("MyService", "http://localhost:8090/MyService");\n\n        Console.WriteLine("Service Registered.");\n    }\n}