using System;\nusing System.Collections;\n\npublic class ReferenceWrapper<T>\n{\n    public T Value { get; set; }\n\n    public ReferenceWrapper(T value)\n    {\n        Value = value;\n    }\n}\n\npublic class Example\n{\n    public string headers_path;\n\n    public void Demo()\n    {\n        Hashtable configMapping = new Hashtable();\n\n        // Wrap the reference\n        ReferenceWrapper<string> headersPathWrapper = new ReferenceWrapper<string>(headers_path);\n        configMapping.Add("HEADERS_PATH", headersPathWrapper);\n\n        // Retrieve and modify the reference\n        ReferenceWrapper<string> retrievedWrapper = (ReferenceWrapper<string>)configMapping["HEADERS_PATH"];\n        retrievedWrapper.Value = "New Path";\n\n        // headers_path will reflect the new value\n        headers_path = retrievedWrapper.Value;\n\n        Console.WriteLine(headers_path); // Outputs: New Path\n    }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        Example example = new Example();\n        example.Demo();\n    }\n}