using System;\nusing System.Management;\n\nnamespace IISWMIExample\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string serverName = "localhost";\n            string metabasePath = "IIS://localhost/W3SVC/1/Root";\n\n            try\n            {\n                ManagementScope scope = new ManagementScope(@"\\.\root\MicrosoftIISv2");\n                scope.Connect();\n\n                ManagementObject website = new ManagementObject(scope, new ManagementPath(metabasePath), null);\n                website.Get();\n\n                Console.WriteLine("Website Name: " + website["ServerComment"]);\n                Console.WriteLine("HTTP Port: " + website["ServerBindings"]);\n\n                // Modify a property\n                website["ServerComment"] = "New Comment";\n                website.Put();\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("Error: " + ex.Message);\n            }\n        }\n    }\n}