private void checkedListBox1_ItemCheck(object sender, ItemCheckEventArgs e)\n{\n    // Specify the indices of the items that you want to make uncheckable.\n    List<int> uncheckableItems = new List<int> { 1, 3, 5 };\n\n    // Check if the item being changed is in the list of uncheckable items.\n    if (uncheckableItems.Contains(e.Index))\n    {\n        // Cancel the change by setting the NewValue to the current value.\n        e.NewValue = e.CurrentValue;\n    }\n}