private bool isDirty = false;\nprivate BindingSource bindingSource;\n\npublic YourForm()\n{\n    InitializeComponent();\n    InitializeDataBinding();\n}\n\nprivate void InitializeDataBinding()\n{\n    // Assuming you have a BindingSource named bindingSource\n    bindingSource = new BindingSource();\n\n    // Bind your controls here\n    textBox1.DataBindings.Add("Text", bindingSource, "YourProperty", true, DataSourceUpdateMode.OnPropertyChanged);\n    comboBox1.DataBindings.Add("SelectedValue", bindingSource, "YourOtherProperty", true, DataSourceUpdateMode.OnPropertyChanged);\n\n    // Subscribe to the BindingComplete event\n    bindingSource.BindingComplete += new BindingCompleteEventHandler(bindingSource_BindingComplete);\n}\n\nprivate void bindingSource_BindingComplete(object sender, BindingCompleteEventArgs e)\n{\n    if (e.BindingCompleteContext == BindingCompleteContext.DataSourceUpdate)\n    {\n        isDirty = true;\n        // You can also perform other actions here, such as enabling a save button\n    }\n}\n\n// Optionally, you can add a method to reset the dirty flag when data is saved\nprivate void SaveData()\n{\n    // Save your data here\n    isDirty = false;\n}