using System;\nusing System.IO;\nusing System.Collections.Generic;\n\nclass Quiz\n{\n    public string Question { get; set; }\n    public List<string> Answers { get; set; }\n    public int CorrectAnswerIndex { get; set; }\n\n    public Quiz(string question, List<string> answers, int correctAnswerIndex)\n    {\n        Question = question;\n        Answers = answers;\n        CorrectAnswerIndex = correctAnswerIndex;\n    }\n\n    public bool AskQuestion()\n    {\n        Console.WriteLine(Question);\n        for (int i = 0; i < Answers.Count; i++)\n        {\n            Console.WriteLine($"{i + 1}: {Answers[i]}");\n        }\n\n        Console.Write("Your answer: ");\n        if (int.TryParse(Console.ReadLine(), out int userAnswerIndex))\n        {\n            return userAnswerIndex - 1 == CorrectAnswerIndex;\n        }\n        return false;\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        string filePath = "quiz.txt";\n        List<Quiz> quizzes = new List<Quiz>();\n\n        if (File.Exists(filePath))\n        {\n            string[] lines = File.ReadAllLines(filePath);\n            for (int i = 0; i < lines.Length; i += 6)\n            {\n                string question = lines[i];\n                List<string> answers = new List<string> \n                { \n                    lines[i + 1], \n                    lines[i + 2], \n                    lines[i + 3], \n                    lines[i + 4] \n                };\n                int correctAnswerIndex = int.Parse(lines[i + 5]) - 1; // Correct answer index in file is 1-based\n                quizzes.Add(new Quiz(question, answers, correctAnswerIndex));\n            }\n\n            foreach (var quiz in quizzes)\n            {\n                bool isCorrect = quiz.AskQuestion();\n                Console.WriteLine(isCorrect ? "Correct!" : "Wrong answer.");\n                Console.WriteLine();\n            }\n        }\n        else\n        {\n            Console.WriteLine("Quiz file not found.");\n        }\n    }\n}