using System;\n   using System.IO;\n   using NAudio.Wave;\n   using NVorbis;\n\n   class Program\n   {\n       static void Main(string[] args)\n       {\n           string wavFilePath = "input.wav";\n           string oggFilePath = "output.ogg";\n           string decompressedWavFilePath = "decompressed.wav";\n\n           // Compress WAV to OGG\n           CompressWavToOgg(wavFilePath, oggFilePath);\n\n           // Decompress OGG back to WAV\n           DecompressOggToWav(oggFilePath, decompressedWavFilePath);\n       }\n\n       static void CompressWavToOgg(string inputWavFilePath, string outputOggFilePath)\n       {\n           using (var reader = new WaveFileReader(inputWavFilePath))\n           using (var writer = new VorbisWaveWriter(outputOggFilePath, reader.WaveFormat))\n           {\n               reader.CopyTo(writer);\n           }\n       }\n\n       static void DecompressOggToWav(string inputOggFilePath, string outputWavFilePath)\n       {\n           using (var vorbis = new VorbisReader(inputOggFilePath))\n           using (var waveStream = new WaveFileWriter(outputWavFilePath, new WaveFormat(vorbis.SampleRate, vorbis.Channels)))\n           {\n               float[] buffer = new float[vorbis.SampleRate * vorbis.Channels];\n               int samplesRead;\n               while ((samplesRead = vorbis.ReadSamples(buffer, 0, buffer.Length)) > 0)\n               {\n                   waveStream.WriteSamples(buffer, 0, samplesRead);\n               }\n           }\n       }\n   }