using System;\nusing System.Web.UI;\nusing System.Web.UI.WebControls;\n\npublic partial class DynamicTextboxes : System.Web.UI.Page\n{\n    private const int PageSize = 10;\n    private int CurrentPage\n    {\n        get { return (int)(ViewState["CurrentPage"] ?? 0); }\n        set { ViewState["CurrentPage"] = value; }\n    }\n\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (IsPostBack)\n        {\n            GenerateTextboxes();\n        }\n    }\n\n    protected void btnGenerate_Click(object sender, EventArgs e)\n    {\n        CurrentPage = 0;\n        GenerateTextboxes();\n    }\n\n    protected void btnPrevious_Click(object sender, EventArgs e)\n    {\n        if (CurrentPage > 0)\n        {\n            CurrentPage--;\n            GenerateTextboxes();\n        }\n    }\n\n    protected void btnNext_Click(object sender, EventArgs e)\n    {\n        int totalTextboxes = int.Parse(txtNumber.Text);\n        if ((CurrentPage + 1) * PageSize < totalTextboxes)\n        {\n            CurrentPage++;\n            GenerateTextboxes();\n        }\n    }\n\n    private void GenerateTextboxes()\n    {\n        int totalTextboxes = int.Parse(txtNumber.Text);\n        textboxContainer.Controls.Clear();\n        int start = CurrentPage * PageSize;\n        int end = Math.Min(start + PageSize, totalTextboxes);\n\n        for (int i = start; i < end; i++)\n        {\n            TextBox textBox = new TextBox();\n            textBox.ID = "TextBox" + i;\n            textBox.CssClass = "textbox-item";\n            textboxContainer.Controls.Add(textBox);\n        }\n    }\n}