using (SqlConnection conn = new SqlConnection(connectionString))\n{\n    using (SqlCommand com = new SqlCommand("GetInfo", conn))\n    {\n        com.CommandType = CommandType.StoredProcedure;\n        com.Parameters.Add(new SqlParameter("@AId", yourAIdValue));\n        conn.Open();\n        \n        using (SqlDataReader reader = com.ExecuteReader())\n        {\n            // Read the first result set (Table A)\n            if (reader.Read())\n            {\n                // Process data from Table A\n                var column1 = reader["Column1"];\n                var column2 = reader["Column2"];\n                // etc.\n            }\n\n            // Move to the next result set (Table B)\n            if (reader.NextResult())\n            {\n                while (reader.Read())\n                {\n                    // Process data from Table B\n                    var column1 = reader["Column1"];\n                    var column2 = reader["Column2"];\n                    // etc.\n                }\n            }\n        }\n    }\n}