using System;\nusing System.Linq;\nusing System.Collections.Generic;\n\npublic class MyClass\n{\n    public int ID { get; set; }\n    public string Name { get; set; }\n    public string OtherProperty { get; set; }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Sample IQueryable object\n        IQueryable<MyClass> queryable = new List<MyClass>\n        {\n            new MyClass { ID = 1, Name = "Name1", OtherProperty = "Other1" },\n            new MyClass { ID = 2, Name = "Name2", OtherProperty = "Other2" }\n        }.AsQueryable();\n\n        // Project the columns and convert to List\n        var list = queryable\n            .Select(s => new { ID = s.ID, Name = s.Name })\n            .ToList();\n\n        // Display the result\n        foreach (var item in list)\n        {\n            Console.WriteLine("ID: " + item.ID + ", Name: " + item.Name);\n        }\n    }\n}