using System.Web.Mvc;\n\npublic class HttpMethodOverrideAttribute : ActionFilterAttribute\n{\n    public override void OnActionExecuting(ActionExecutingContext filterContext)\n    {\n        var request = filterContext.HttpContext.Request;\n        var httpMethodOverride = request.Headers["X-HTTP-Method-Override"];\n        \n        if (!string.IsNullOrEmpty(httpMethodOverride))\n        {\n            request.GetType().GetProperty("HttpMethod").SetValue(request, httpMethodOverride, null);\n        }\n\n        base.OnActionExecuting(filterContext);\n    }\n}