using System;\nusing Microsoft.Office.Interop.Word;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a new instance of Word application\n        Application wordApp = new Application();\n        \n        // Path to the Word document\n        string documentPath = @"C:\Path\To\Your\Document.docx";\n        \n        // Open the Word document\n        Document doc = wordApp.Documents.Open(documentPath);\n        \n        // Make Word application visible (optional)\n        wordApp.Visible = true;\n        \n        // Fill in the form fields\n        foreach (FormField field in doc.FormFields)\n        {\n            switch (field.Name)\n            {\n                case "FieldName1":\n                    field.Result = "Value1";\n                    break;\n                case "FieldName2":\n                    field.Result = "Value2";\n                    break;\n                // Add more fields as needed\n            }\n        }\n        \n        // Print the document\n        doc.PrintOut();\n\n        // Save the changes (optional)\n        doc.Save();\n        \n        // Close the document\n        doc.Close();\n\n        // Quit the Word application\n        wordApp.Quit();\n        \n        // Release COM objects\n        System.Runtime.InteropServices.Marshal.ReleaseComObject(doc);\n        System.Runtime.InteropServices.Marshal.ReleaseComObject(wordApp);\n    }\n}