using System;\nusing System.ComponentModel;\nusing System.Configuration.Install;\n\n[RunInstaller(true)]\npublic class CustomInstaller : Installer\n{\n    public override void Install(System.Collections.IDictionary stateSaver)\n    {\n        base.Install(stateSaver);\n        string siteName = Context.Parameters["SiteName"];\n        string sitePath = Context.Parameters["SitePath"];\n        string ipAddress = Context.Parameters["IPAddress"];\n        string hostHeader = Context.Parameters["HostHeader"];\n        int port = int.Parse(Context.Parameters["Port"]);\n\n        IISManager.CreateWebsite(siteName, sitePath, ipAddress, hostHeader, port);\n    }\n}