var binding = new WSHttpBinding(SecurityMode.Transport)\n{\n    Security = { Transport = { ClientCredentialType = HttpClientCredentialType.Windows } }\n};\n\nvar endpointAddress = new EndpointAddress("https://yourserver:port/YourService");\n\nvar client = new YourServiceClient(binding, endpointAddress);\n\n// Set client credentials if needed\nclient.ClientCredentials.Windows.ClientCredential = new NetworkCredential("username", "password", "domain");\n\ntry\n{\n    // Call your service methods\n    client.YourServiceMethod();\n}\ncatch (Exception ex)\n{\n    // Handle exceptions\n    Console.WriteLine(ex.Message);\n}