[HttpPost]\n    public ActionResult UploadFilesUsingCookies(IEnumerable<HttpPostedFileBase> files)\n    {\n        var uploadedFiles = new List<string>();\n\n        if (files != null && files.Any())\n        {\n            foreach (var file in files)\n            {\n                if (file != null && file.ContentLength > 0)\n                {\n                    var fileName = Path.GetFileName(file.FileName);\n                    var path = Path.Combine(Server.MapPath("~/App_Data/Uploads"), fileName);\n                    file.SaveAs(path);\n\n                    uploadedFiles.Add(fileName);\n                }\n            }\n\n            var cookieValue = string.Join(",", uploadedFiles);\n            HttpCookie cookie = new HttpCookie("UploadedFiles", cookieValue)\n            {\n                Expires = DateTime.Now.AddHours(1)\n            };\n\n            Response.Cookies.Add(cookie);\n        }\n\n        return RedirectToAction("UploadResultUsingCookies");\n    }\n\n    public ActionResult UploadResultUsingCookies()\n    {\n        var uploadedFiles = new List<string>();\n        var cookie = Request.Cookies["UploadedFiles"];\n        if (cookie != null)\n        {\n            uploadedFiles = cookie.Value.Split(',').ToList();\n        }\n\n        return View(uploadedFiles);\n    }