// Default.aspx.cs\npublic partial class _Default : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            // Subscribe to the event\n            SearchControl.SearchResultsUpdated += SearchControl_SearchResultsUpdated;\n        }\n    }\n\n    private void SearchControl_SearchResultsUpdated(object sender, SearchEventArgs e)\n    {\n        // Pass the search results to the product control\n        ProductControl.UpdateProducts(e.Results);\n    }\n}