using System;\nusing System.DirectoryServices;\n\npublic class LdapConnectionChecker\n{\n    public static bool IsConnected(string ldapPath, string username, string password)\n    {\n        try\n        {\n            using (DirectoryEntry entry = new DirectoryEntry(ldapPath, username, password))\n            {\n                // Perform a simple search to validate the connection\n                using (DirectorySearcher searcher = new DirectorySearcher(entry))\n                {\n                    searcher.Filter = "(objectClass=*)";\n                    searcher.PageSize = 1; // Limiting the search results to 1 for efficiency\n                    searcher.FindOne(); // This will throw an exception if the connection is not valid\n                }\n            }\n            return true; // Connection is valid\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Connection failed: {ex.Message}");\n            return false; // Connection is not valid\n        }\n    }\n}