using System;\nusing System.Web.Compilation;\nusing System.Web.UI;\n\n[ExpressionPrefix("Config")]\npublic class ConfigExpressionBuilder : ExpressionBuilder\n{\n    public override object EvaluateExpression(object target, BoundPropertyEntry entry, object parsedData, ExpressionBuilderContext context)\n    {\n        return System.Configuration.ConfigurationManager.AppSettings[entry.Expression.Trim()];\n    }\n\n    public override bool SupportsEvaluate => true;\n}