using System;\nusing System.Data.SqlClient;\n\npublic void LogUserActivity(int userId, DateTime loginDateTime, DateTime? logoutDateTime)\n{\n    string connectionString = "your_connection_string_here";\n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        connection.Open();\n        string query = "INSERT INTO UserLogins (UserId, LoginDateTime, LogoutDateTime) VALUES (@UserId, @LoginDateTime, @LogoutDateTime)";\n        using (SqlCommand command = new SqlCommand(query, connection))\n        {\n            command.Parameters.AddWithValue("@UserId", userId);\n            command.Parameters.AddWithValue("@LoginDateTime", loginDateTime);\n            command.Parameters.AddWithValue("@LogoutDateTime", (object)logoutDateTime ?? DBNull.Value);\n            command.ExecuteNonQuery();\n        }\n    }\n}