using System;\nusing MailKit.Net.Smtp;\nusing MimeKit;\nusing MailKit.Security;\n\npublic class EmailSender\n{\n    public void SendEmail()\n    {\n        var message = new MimeMessage();\n        message.From.Add(new MailboxAddress("Your Name", "emailAddress@mydomain.example"));\n        message.To.Add(new MailboxAddress("Recipient Name", "someone@somewhere.example"));\n        message.Subject = "Test";\n\n        message.Body = new TextPart("html")\n        {\n            Text = "<html><body>This is a test</body></html>"\n        };\n\n        using (var client = new SmtpClient())\n        {\n            client.Connect("smtp.gmail.com", 587, SecureSocketOptions.StartTls);\n\n            // Note: only needed if the SMTP server requires authentication\n            client.Authenticate("emailAddress@mydomain.example", "appSpecificPassword");\n\n            client.Send(message);\n            client.Disconnect(true);\n        }\n\n        Console.WriteLine("Email sent successfully");\n    }\n}