// SearchControl.ascx.cs\npublic partial class SearchControl : System.Web.UI.UserControl\n{\n    public event EventHandler<SearchEventArgs> SearchResultsUpdated;\n\n    protected void OnSearchButtonClick(object sender, EventArgs e)\n    {\n        // Perform search logic and get the results\n        var searchResults = PerformSearch();\n\n        // Raise the event\n        if (SearchResultsUpdated != null)\n        {\n            SearchResultsUpdated(this, new SearchEventArgs { Results = searchResults });\n        }\n    }\n\n    private List<string> PerformSearch()\n    {\n        // Dummy search logic\n        return new List<string> { "Product1", "Product2" };\n    }\n}\n\npublic class SearchEventArgs : EventArgs\n{\n    public List<string> Results { get; set; }\n}