// Set the session variables with timestamps\nSession["var1"] = new Tuple<object, DateTime>(value1, DateTime.Now.AddMinutes(480));\nSession["var2"] = new Tuple<object, DateTime>(value2, DateTime.Now.AddMinutes(480));\nSession["var3"] = new Tuple<object, DateTime>(value3, DateTime.Now.AddMinutes(480));\nSession["var4"] = new Tuple<object, DateTime>(value4, DateTime.Now.AddMinutes(480));\nSession["var5"] = new Tuple<object, DateTime>(value5, DateTime.Now.AddMinutes(20));\n\n// Retrieve session variables with check for expiration\nobject GetSessionVariable(string key)\n{\n    var sessionData = Session[key] as Tuple<object, DateTime>;\n    if (sessionData != null)\n    {\n        if (DateTime.Now <= sessionData.Item2)\n        {\n            return sessionData.Item1; // Return the value if not expired\n        }\n        else\n        {\n            Session.Remove(key); // Remove the expired session variable\n        }\n    }\n    return null; // Return null if expired or not set\n}\n\n// Usage example\nvar var1 = GetSessionVariable("var1");\nvar var5 = GetSessionVariable("var5");