using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic void CreateUser(string username, string password)\n{\n    string connectionString = "your_connection_string";\n    using (SqlConnection conn = new SqlConnection(connectionString))\n    {\n        using (SqlCommand cmd = new SqlCommand("CreateUser", conn))\n        {\n            cmd.CommandType = CommandType.StoredProcedure;\n            cmd.Parameters.AddWithValue("@Username", username);\n            cmd.Parameters.AddWithValue("@Password", password);\n\n            SqlParameter errorMsgParam = new SqlParameter("@ErrorMsg", SqlDbType.NVarChar, 255)\n            {\n                Direction = ParameterDirection.Output\n            };\n            cmd.Parameters.Add(errorMsgParam);\n\n            try\n            {\n                conn.Open();\n                int result = (int)cmd.ExecuteScalar();\n\n                string errorMsg = (string)errorMsgParam.Value;\n                if (result == 0)\n                {\n                    Console.WriteLine("Success: " + errorMsg);\n                }\n                else\n                {\n                    Console.WriteLine("Error: " + errorMsg);\n                }\n            }\n            catch (SqlException ex)\n            {\n                Console.WriteLine("SQL Error: " + ex.Message);\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("General Error: " + ex.Message);\n            }\n        }\n    }\n}