using System;\nusing System.Reflection;\nusing System.Security.Policy;\n\nclass Program\n{\n    static void Main()\n    {\n        AppDomainSetup setup = new AppDomainSetup();\n        setup.ApplicationBase = AppDomain.CurrentDomain.BaseDirectory;\n        setup.PrivateBinPath = "path_to_your_private_bin";\n\n        AppDomain newDomain = AppDomain.CreateDomain("NewDomain", null, setup);\n\n        // Example of loading an assembly in the new domain\n        string assemblyPath = "path_to_your_assembly.dll";\n        newDomain.ExecuteAssembly(assemblyPath);\n\n        // Unload the domain when done\n        AppDomain.Unload(newDomain);\n    }\n}