using Microsoft.Win32;\n\npublic void UpdateRegistryKey()\n{\n    try\n    {\n        // Open a subkey within HKEY_CURRENT_USER\n        RegistryKey key = Registry.CurrentUser.OpenSubKey(@"Software\YourApplication", true);\n        \n        if (key == null)\n        {\n            // Create the subkey if it does not exist\n            key = Registry.CurrentUser.CreateSubKey(@"Software\YourApplication");\n        }\n\n        // Set a value in the subkey\n        key.SetValue("YourSetting", "YourValue");\n        key.Close();\n    }\n    catch (Exception ex)\n    {\n        // Handle exception\n        Console.WriteLine("An error occurred: " + ex.Message);\n    }\n}