using System;\nusing System.Media;\nusing System.Threading.Tasks;\nusing System.Windows.Forms;\n\npublic partial class MainForm : Form\n{\n    public MainForm()\n    {\n        InitializeComponent();\n    }\n\n    private async void PlaySoundsAndChangeImages()\n    {\n        await PlaySoundAsync("sound1.wav");\n        ChangeImage(pictureBox1, "image1.png");\n        \n        await PlaySoundAsync("sound2.wav");\n        ChangeImage(pictureBox2, "image2.png");\n        \n        await PlaySoundAsync("sound3.wav");\n        ChangeImage(pictureBox3, "image3.png");\n    }\n\n    private Task PlaySoundAsync(string soundFile)\n    {\n        return Task.Run(() =>\n        {\n            using (SoundPlayer player = new SoundPlayer(soundFile))\n            {\n                player.PlaySync();\n            }\n        });\n    }\n\n    private void ChangeImage(PictureBox pictureBox, string imageFile)\n    {\n        pictureBox.Image = Image.FromFile(imageFile);\n    }\n}