using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class MyObject\n{\n    public int Id { get; set; }\n    public int? ParentId { get; set; }\n    public string Name { get; set; }\n    // Other properties...\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Example data\n        List<MyObject> objects = new List<MyObject>\n        {\n            new MyObject { Id = 1, ParentId = null, Name = "Root" },\n            new MyObject { Id = 2, ParentId = 1, Name = "Child 1" },\n            new MyObject { Id = 3, ParentId = 1, Name = "Child 2" },\n            // ... more objects\n        };\n\n        // Example LINQ query to find all children of the root object\n        var rootChildren = objects.Where(o => o.ParentId == 1).ToList();\n\n        foreach (var child in rootChildren)\n        {\n            Console.WriteLine($"Child Id: {child.Id}, Name: {child.Name}");\n        }\n    }\n}