using System;\n\nclass Program\n{\n    static void Main()\n    {\n        DateTime d1 = new DateTime(2007, 12, 1);\n        DateTime d2 = new DateTime(2012, 6, 30);\n\n        int totalDays = (d2 - d1).Days;\n        int leapYearDays = CalculateLeapYearDays(d1, d2);\n\n        Console.WriteLine("Total days: " + totalDays);\n        Console.WriteLine("Leap year days: " + leapYearDays);\n    }\n\n    static bool IsLeapYear(int year)\n    {\n        return (year % 4 == 0 && year % 100 != 0) || (year % 400 == 0);\n    }\n\n    static int CalculateLeapYearDays(DateTime start, DateTime end)\n    {\n        int leapYearDays = 0;\n\n        for (int year = start.Year; year <= end.Year; year++)\n        {\n            if (IsLeapYear(year))\n            {\n                DateTime leapYearStart = new DateTime(year, 1, 1);\n                DateTime leapYearEnd = new DateTime(year, 12, 31);\n\n                if (start > leapYearStart) leapYearStart = start;\n                if (end < leapYearEnd) leapYearEnd = end;\n\n                leapYearDays += (leapYearEnd - leapYearStart).Days + 1;\n            }\n        }\n\n        return leapYearDays;\n    }\n}