using Windows.Security.Credentials;\n\n   public class CredentialManager\n   {\n       public static void SaveCredentials(string resource, string userName, string password)\n       {\n           var vault = new PasswordVault();\n           var credential = new PasswordCredential(resource, userName, password);\n           vault.Add(credential);\n       }\n\n       public static string RetrieveCredentials(string resource, string userName)\n       {\n           var vault = new PasswordVault();\n           var credential = vault.Retrieve(resource, userName);\n           credential.RetrievePassword();\n           return credential.Password;\n       }\n   }