public class FilesController : Controller\n{\n    public ActionResult Index()\n    {\n        // Ideally, fetch this from your database\n        var files = new List<FileModel>\n        {\n            new FileModel { FileID = 1, FileName = "test.txt", FileSize = 100, Group = "Group1" },\n            new FileModel { FileID = 2, FileName = "test2.txt", FileSize = 100, Group = "Group1" },\n            new FileModel { FileID = 3, FileName = "test3.txt", FileSize = 100, Group = "Group2" }\n        };\n\n        // Group by the Group property\n        var groupedFiles = files.GroupBy(f => f.Group);\n\n        return View(groupedFiles);\n    }\n}