[WebMethod]\n   public void PerformAction()\n   {\n       var userId = GetCurrentUserId();\n       var authorizationService = new AuthorizationService(new UserRepository(), new RoleRepository());\n\n       if (!authorizationService.IsUserAuthorized(userId, "PerformAction"))\n       {\n           throw new UnauthorizedAccessException("User is not authorized to perform this action.");\n       }\n\n       // Perform the action here\n   }