protected void Page_Load(object sender, EventArgs e)\n   {\n       var userId = GetCurrentUserId();\n       var authorizationService = new AuthorizationService(new UserRepository(), new RoleRepository());\n\n       if (!authorizationService.IsUserAuthorized(userId, "ViewYButton"))\n       {\n           YButton.Visible = false;\n       }\n\n       if (!authorizationService.IsUserAuthorized(userId, "ViewBButton"))\n       {\n           BButton.Visible = false;\n       }\n   }