public class HomeController : Controller\n{\n    public async Task<ActionResult> Index()\n    {\n        string url = "http://example.com/Members/newsletters/welcome.html";\n        string content;\n\n        try\n        {\n            content = await ReadFromUrlAsync(url);\n        }\n        catch (HttpRequestException ex)\n        {\n            // Handle exception (log it, show a friendly message, etc.)\n            content = "Error retrieving content.";\n        }\n\n        ViewBag.WelcomeContent = content;\n        return View();\n    }\n}