DataTable table = new DataTable();\ntable.Columns.Add("ID", typeof(int));\ntable.Columns.Add("Date", typeof(DateTime));\n\n// Add some sample data\ntable.Rows.Add(1, new DateTime(2023, 10, 1));\ntable.Rows.Add(2, new DateTime(2023, 10, 2));\ntable.Rows.Add(3, new DateTime(2023, 10, 3));\n\n// Define the date you are searching for\nDateTime searchDate = new DateTime(2023, 10, 2);\nstring dateString = searchDate.ToString("yyyy-MM-dd");\n\n// Use the Select method to find matching rows\nDataRow[] foundRows = table.Select($"Date = #{dateString}#");\n\n// Process the found rows\nforeach (DataRow row in foundRows)\n{\n    Console.WriteLine($"ID: {row["ID"]}, Date: {row["Date"]}");\n}