using System;\nusing System.Linq;\n\npublic class DecimalToWholeNumberConverter\n{\n    public static int FindSmallestCommonMultiplier(decimal[] numbers)\n    {\n        int maxDecimalPlaces = numbers.Max(num => GetDecimalPlaces(num));\n        long scaleFactor = (long)Math.Pow(10, maxDecimalPlaces);\n\n        long[] scaledNumbers = numbers.Select(num => (long)(num * scaleFactor)).ToArray();\n        long gcd = scaledNumbers.Aggregate(GCD);\n\n        return (int)(scaleFactor / gcd);\n    }\n\n    private static int GetDecimalPlaces(decimal number)\n    {\n        int decimalPlaces = BitConverter.GetBytes(decimal.GetBits(number)[3])[2];\n        return decimalPlaces;\n    }\n\n    private static long GCD(long a, long b)\n    {\n        while (b != 0)\n        {\n            long temp = b;\n            b = a % b;\n            a = temp;\n        }\n        return a;\n    }\n\n    public static void Main()\n    {\n        decimal[] numbers = { 0.12345678m, 1.23456789m, 0.00000001m };\n        int multiplier = FindSmallestCommonMultiplier(numbers);\n        Console.WriteLine("Smallest common multiplier: " + multiplier);\n    }\n}