using System.Data.Common;\n\nclass Program\n{\n    static void Main()\n    {\n        DataAccessLayer dal = new DataAccessLayer("AccessDb");\n\n        var usersTable = dal.ExecuteQuery("GetUsersQuery");\n\n        foreach (DataRow row in usersTable.Rows)\n        {\n            Console.WriteLine($"User: {row["Name"]}, Email: {row["Email"]}");\n        }\n\n        DbParameter nameParam = DbProviderFactories.GetFactory("System.Data.OleDb").CreateParameter();\n        nameParam.ParameterName = "@Name";\n        nameParam.Value = "John Doe";\n\n        DbParameter emailParam = DbProviderFactories.GetFactory("System.Data.OleDb").CreateParameter();\n        emailParam.ParameterName = "@Email";\n        emailParam.Value = "john.doe@example.com";\n\n        int rowsAffected = dal.ExecuteNonQuery("InsertUserQuery", nameParam, emailParam);\n\n        Console.WriteLine($"{rowsAffected} row(s) inserted.");\n    }\n}