protected void Page_Load(object sender, EventArgs e)\n{\n    // Recreate dynamic controls on every postback\n    if (IsPostBack)\n    {\n        RecreateDynamicControls();\n    }\n}\n\nprotected void Button1_Click(object sender, EventArgs e)\n{\n    AddNewLiteralControl();\n}\n\nprivate void AddNewLiteralControl()\n{\n    Literal literal = new Literal();\n    literal.Text = DateTime.Now.ToString();\n    literal.ID = DateTime.Now.Ticks.ToString();\n\n    PlaceHolder1.Controls.Add(literal);\n\n    // Store control information in ViewState to recreate on postback\n    List<string> controls = ViewState["DynamicControls"] as List<string> ?? new List<string>();\n    controls.Add(literal.Text);\n    ViewState["DynamicControls"] = controls;\n}\n\nprivate void RecreateDynamicControls()\n{\n    List<string> controls = ViewState["DynamicControls"] as List<string>;\n    if (controls != null)\n    {\n        foreach (string text in controls)\n        {\n            Literal literal = new Literal();\n            literal.Text = text;\n            literal.ID = DateTime.Now.Ticks.ToString();\n\n            PlaceHolder1.Controls.Add(literal);\n        }\n    }\n}